const contentBlock = $json.data[0].content[0].text.value;

// Try to extract JSON block using regex
const match = contentBlock.match(/```json([\s\S]*?)```/);

if (match) {
  try {
    const parsed = JSON.parse(match[1].trim());
    return [{ json: parsed }];
  } catch (err) {
    return [{ json: { error: "Failed to parse JSON block", rawContent: contentBlock } }];
  }
} else {
  return [{ json: { error: "No JSON block found", rawContent: contentBlock } }];
}

const messages = $json.data || [];

// Defensive: Check if messages is a non-empty array
if (!Array.isArray(messages) || messages.length === 0) {
  throw new Error("No messages found in input data. Input: " + JSON.stringify($json, null, 2));
}

// Find the last assistant message
const last = messages.find(m => m.role === 'assistant');

// Defensive: Log messages for debugging if assistant message is missing or malformed
if (!last || !last.content || !last.content[0] || !last.content[0].text || !last.content[0].text.value) {
  console.log("DEBUG: messages = ", JSON.stringify(messages, null, 2));
  throw new Error("No valid assistant response found");
}

let raw = last.content[0].text.value.trim();

// Remove Markdown code block wrapper if present
if (raw.startsWith("```json")) {
  raw = raw.replace(/^```json/, "").replace(/```$/, "").trim();
}

// Sanity check: JSON should still start and end correctly
if (!raw.startsWith("{") || !raw.endsWith("}")) {
  throw new Error("Assistant response is not valid JSON:\n\n" + raw);
}

let parsed;
try {
  parsed = JSON.parse(raw);
} catch (err) {
  throw new Error("Failed to parse JSON:\n\n" + raw);
}

const { slug, prompts } = parsed;

// Defensive: Check that prompts is an array
if (!Array.isArray(prompts)) {
  throw new Error("Parsed assistant response does not contain a valid 'prompts' array:\n\n" + raw);
}

const outputs = prompts.map(p => ({
  json: {
    slug,
    label: p.label,
    prompt: p.text
  }
}));

return outputs;

function decodeBase64(str) {
  return Buffer.from(str.replace(/-/g, '+').replace(/_/g, '/'), 'base64').toString('utf-8');
}

// Extract the plain text part from the Gmail payload
const parts = $json.payload?.parts || [];
const textPart = parts.find(p => p.mimeType === 'text/plain');
const encoded = textPart?.body?.data;

const decodedBody = encoded ? decodeBase64(encoded) : '';

// Look for IDEA format: "--- IDEA X ---" instead of "Choice: X"
const ideaMatch = decodedBody.match(/---\s*IDEA\s*(\d+)\s*---/i);
const threadMatch = decodedBody.match(/thread_[\w-]+/i);
const notesMatch = decodedBody.match(/Notes[:：]?\s*([\s\S]*?)(?:\r?\nOn\s|\r?\n>|\r?\n?$)/i);

const choice = ideaMatch ? parseInt(ideaMatch[1], 10) : null;
const thread_id = threadMatch?.[0] || null;
const notes = notesMatch?.[1]?.trim() || null;

// Extract selected IDEA block
let selectedIdeaBlock = null;
if (choice) {
  const ideaRegex = new RegExp(
    `---\\s*IDEA\\s*${choice}\\s*---([\\s\\S]*?)(?=\\n---\\s*IDEA\\s*\\d+\\s*---|\\n?$)`,
    'i'
  );
  const match = decodedBody.match(ideaRegex);
  selectedIdeaBlock = match?.[1]?.trim() || null;
}

const fieldLabels = {
  'Title': 'title',
  'Meta Description': 'metaDescription',
  'Primary Keywords': 'primaryKeywords',
  'Secondary Keywords': 'secondaryKeywords',
  'Affiliate Product Category': 'affiliateCategory',
  'Recommended Cluster Tags': 'clusterTags',
  'Applied Style Lens': 'styleLens',
  'Internal Link Targets': 'internalLinks',
};

const extractedFields = {};
if (selectedIdeaBlock) {
  for (const [label, key] of Object.entries(fieldLabels)) {
    const regex = new RegExp(`\\*?${label}\\*?[:：]?\\s*([\\s\\S]*?)(?=\\n\\*?[A-Z][^:\\n]{2,30}\\*?[:：]|\\n?$)`, 'i');
    const match = selectedIdeaBlock.match(regex);
    extractedFields[key] = match ? match[1].trim().replace(/\r?\n/g, ' ') : null;
  }

  // Convert comma-separated lists into arrays
  if (extractedFields.clusterTags) {
    extractedFields.clusterTags = extractedFields.clusterTags.split(',').map(s => s.trim());
  }
  if (extractedFields.internalLinks) {
    extractedFields.internalLinks = extractedFields.internalLinks.split(',').map(s => s.trim());
  }
}

return [
  {
    json: {
      thread_id,
      choice,
      notes,
      selectedIdea: selectedIdeaBlock,
      includesChoice: choice !== null,
      internalDate: $json.internalDate || null,
      decodedBody, // Optional: remove when stable
      ...extractedFields
    }
  }
];

const raw = $json.content?.[0]?.text || '{}';

let parsed = {};
try {
  parsed = JSON.parse(raw);
} catch (err) {
  parsed = { error: 'Failed to parse JSON', raw };
}

return [
  {
    json: {
      ...parsed,
      includesChoice: parsed.choice !== null && parsed.choice !== undefined,
    },
  },
];

return [
  {
    json: {
      role: "user",
      content: [
        {
          type: "text",
          text: JSON.stringify({
            thread_id: $json.thread_id || null,
            choice: $json.choice || null,
            notes: $json.notes || null,
            title: $json.title || null,
            metaDescription: $json.metaDescription || null,
            primaryKeywords: $json.primaryKeywords || null,
            secondaryKeywords: $json.secondaryKeywords || null,
            affiliateCategory: $json.affiliateCategory || null,
            clusterTags: $json.clusterTags || [],
            styleLens: $json.styleLens || null,
            internalLinks: $json.internalLinks || []
          }, null, 2) // pretty-printed JSON
        }
      ]
    }
  }
];

const threadId = $json.id; // from HTTP Request2
const input = $node["Code5"].json; // ← change this to the correct node

const messageText = `
Title: ${input.title}
Meta Description: ${input.metaDescription}
Primary Keywords: ${input.primaryKeywords}
Secondary Keywords: ${input.secondaryKeywords}
Affiliate Category: ${input.affiliateCategory}
Cluster Tags: ${(input.clusterTags || []).join(", ")}
Style Lens: ${input.styleLens}
Internal Links: ${(input.internalLinks || []).join(", ")}
Notes: ${input.notes}
Choice: ${input.choice}
Thread ID: ${threadId}
`.trim();

return [
  {
    json: {
      thread_id: threadId,
      message: {
        role: "user",
        content: [
          {
            type: "text",
            text: messageText
          }
        ]
      }
    }
  }
];

const threadId = $node["HTTP Request13"].json["id"];
const input = $node["If3"].json;
const title = input.title || "Untitled Blog";
const meta = input.metaDescription || input.notes || "No description provided.";

return [
  {
    json: {
      thread_id: threadId,
      message: {
        role: "user",
        content: [
          {
            type: "text",
            text: `Title: ${title}\nExcerpt: ${meta}`
          }
        ]
      }
    }
  }
];

const messages = $input.all();
let latestValid = null;

for (const item of messages) {
  const snippet = item.json.snippet || '';
  // Look for IDEA format: "--- IDEA X ---" instead of "Choice: X"
  const hasIdea = /---\s*IDEA\s*\d+\s*---/i.test(snippet);

  if (hasIdea) {
    const ideaMatch = snippet.match(/---\s*IDEA\s*(\d+)\s*---/i);
    const threadMatch = snippet.match(/thread_[\w-]+/i);

    if (ideaMatch && threadMatch) {
      latestValid = {
        id: item.json.id,
        threadId: item.json.threadId,
        thread_id: threadMatch[0],
        choice: parseInt(ideaMatch[1], 10),
        snippet: snippet,
        internalDate: item.json.internalDate,
      };
      break; // stop at the first valid one
    }
  }
}

return latestValid ? [ { json: latestValid } ] : [];

if ($binary && $binary.data && $binary.data.data) {
  const buffer = Buffer.from($binary.data.data, 'base64');
  return {
    json: {
      ...$json,
      content: buffer.toString('base64'),
      binary_size: buffer.length,
    },
    binary: {
      data: $binary.data,
    }
  };
} else {
  return {
    json: {
      ...$json,
      error: 'No binary data found'
    }
  };
}
