// Parse blog post data for social media
const blogPost = $input.first().json;

// Extract key information for social media
const postData = {
  title: blogPost.title,
  description: blogPost.description,
  slug: blogPost.slug,
  url: `https://bright-gift.com/blog/${blogPost.slug}`,
  themes: blogPost.tags || [],
  keywords: blogPost.keywords || [],
  image: blogPost.image,
  socialImage: blogPost.socialImage,
  ogImage: blogPost.ogImage,
  bannerImage: blogPost.bannerImage,
  category: blogPost.category,
  date: blogPost.date
};

// Determine target audience based on tags
const audience = [];
if (postData.themes.includes('budget-friendly') || postData.themes.includes('under-50')) {
  audience.push('budget conscious');
}
if (postData.themes.includes('handmade') || postData.themes.includes('etsy')) {
  audience.push('handmade lovers');
}
if (postData.themes.includes('eco-friendly') || postData.themes.includes('sustainable')) {
  audience.push('eco conscious');
}
if (postData.themes.includes('tech') || postData.themes.includes('gadgets')) {
  audience.push('tech enthusiasts');
}

// Extract key quotes or highlights from description
const keyQuotes = [postData.description];

return {
  postData: {
    ...postData,
    audience,
    keyQuotes,
    affiliateLinks: ['amazon', 'etsy'],
    publishingTimestamp: new Date().toISOString()
  }
};

// Select best existing image or determine if generation is needed
const postData = $input.first().json.postData;

// Priority order for social media images
let selectedImage = null;
let imageType = null;
let needsGeneration = false;

if (postData.socialImage) {
  selectedImage = postData.socialImage;
  imageType = 'social';
} else if (postData.ogImage) {
  selectedImage = postData.ogImage;
  imageType = 'og';
} else if (postData.bannerImage) {
  selectedImage = postData.bannerImage;
  imageType = 'banner';
} else {
  needsGeneration = true;
  imageType = 'generated';
}

// Ensure full URL
if (selectedImage && !selectedImage.startsWith('http')) {
  selectedImage = `https://bright-gift.com${selectedImage}`;
}

return {
  selectedImage,
  imageType,
  imageUrl: selectedImage,
  needsGeneration,
  postData
};

// Process generated image or use existing image
const input = $input.first().json;

let finalImageUrl;
let imageType;

if (input.url) {
  // Newly generated image
  finalImageUrl = input.url;
  imageType = 'generated';
} else {
  // Using existing image
  finalImageUrl = input.imageUrl;
  imageType = input.imageType;
}

return {
  imageUrl: finalImageUrl,
  imageType,
  postData: input.postData || $('Image Selection').first().json.postData
};

// Prepare image for all social media platforms
const input = $input.first().json;
const imageUrl = input.imageUrl;
const postData = input.postData;

// Platform-specific image handling
const platforms = [
  { name: 'instagram', ratio: '1:1', crop: 'none' },
  { name: 'twitter', ratio: '16:9', crop: 'center' },
  { name: 'facebook', ratio: '1.91:1', crop: 'center' },
  { name: 'linkedin', ratio: '1.91:1', crop: 'center' },
  { name: 'pinterest', ratio: '2:3', crop: 'center' }
];

return {
  universalImage: imageUrl,
  platforms,
  postData,
  imageType: input.imageType
};

// Parse AI-generated social media content
const aiResponse = $input.first().json.choices[0].message.content;
const postData = $('Image Optimization').first().json.postData;

// Extract platform-specific content
const platforms = ['twitter', 'linkedin', 'instagram', 'facebook', 'pinterest'];
const socialPosts = {};

// Parse the AI response to extract content for each platform
// This is a simplified parser - you may need to adjust based on AI output format
platforms.forEach(platform => {
  const platformRegex = new RegExp(`${platform}[:\s]*([\s\S]*?)(?=\n\w+:|$)`, 'i');
  const match = aiResponse.match(platformRegex);

  if (match) {
    socialPosts[platform] = {
      content: match[1].trim(),
      imageUrl: $('Image Optimization').first().json.universalImage,
      url: postData.url,
      platform
    };
  } else {
    // Fallback content if parsing fails
    socialPosts[platform] = {
      content: `Check out our latest gift guide: ${postData.title} - ${postData.url}`,
      imageUrl: $('Image Optimization').first().json.universalImage,
      url: postData.url,
      platform
    };
  }
});

return {
  socialPosts,
  postData,
  totalPosts: Object.keys(socialPosts).length
};

// Calculate optimal posting times and schedule posts
const socialPosts = $input.first().json.socialPosts;
const postData = $input.first().json.postData;

// Platform-specific posting times (in hours from now)
const postingSchedule = {
  twitter: [2, 6, 10], // 2, 6, 10 hours from now
  linkedin: [4, 8], // 4, 8 hours from now
  instagram: [3, 7], // 3, 7 hours from now
  facebook: [5, 9], // 5, 9 hours from now
  pinterest: [1, 11] // 1, 11 hours from now
};

const scheduledPosts = [];
const now = new Date();

Object.entries(socialPosts).forEach(([platform, post]) => {
  const times = postingSchedule[platform] || [2];

  times.forEach(hoursFromNow => {
    const scheduledTime = new Date(now.getTime() + (hoursFromNow * 60 * 60 * 1000));

    scheduledPosts.push({
      platform,
      content: post.content,
      image: post.imageUrl,
      url: post.url,
      scheduledTime: scheduledTime.toISOString(),
      postId: `${platform}_${Date.now()}_${hoursFromNow}`
    });
  });
});

return {
  scheduledPosts,
  totalPosts: scheduledPosts.length,
  postData
};

// Collect results from all social media posts
const results = $input.all();
const postData = $('Posting Schedule Manager').first().json.postData;

const postedResults = results.map(result => {
  const data = result.json;
  return {
    platform: data.platform || 'unknown',
    success: !data.error,
    postId: data.id || data.postId || null,
    url: data.url || null,
    timestamp: new Date().toISOString(),
    error: data.error || null
  };
});

const summary = {
  totalPosts: postedResults.length,
  successfulPosts: postedResults.filter(r => r.success).length,
  failedPosts: postedResults.filter(r => !r.success).length,
  postData,
  results: postedResults
};

return summary;
