// Parse blog content and extract topics
const posts = $input.all();
const existingTopics = [];

for (const post of posts) {
  if (post.json && post.json.data) {
    const data = post.json.data;
    if (data.title) existingTopics.push(data.title);
    if (data.tags && Array.isArray(data.tags)) {
      existingTopics.push(...data.tags);
    }
    if (data.keywords && Array.isArray(data.keywords)) {
      existingTopics.push(...data.keywords);
    }
  }
}

// Remove duplicates
const uniqueTopics = [...new Set(existingTopics)];

return {
  existingTopics: uniqueTopics,
  totalPosts: posts.length
};

// Parse OpenAI response and extract topics
try {
  const response = $input.first().json;
  const content = response.choices[0].message.content;
  const topics = JSON.parse(content);

  return {
    seedTopics: topics,
    count: topics.length
  };
} catch (error) {
  throw new Error(`Failed to parse topics: ${error.message}`);
}

// Parse keywords from OpenAI response
try {
  const response = $input.first().json;
  const content = response.choices[0].message.content;
  const keywords = JSON.parse(content);

  return keywords.map(keyword => ({
    keyword: keyword,
    topic: $('Split Topics Batch').item.json
  }));
} catch (error) {
  throw new Error(`Failed to parse keywords: ${error.message}`);
}

// Flatten and validate all keywords
const allItems = $input.all();
const flattenedKeywords = [];

for (const item of allItems) {
  if (Array.isArray(item.json)) {
    flattenedKeywords.push(...item.json);
  } else {
    flattenedKeywords.push(item.json);
  }
}

// Remove duplicates
const uniqueKeywords = [];
const seen = new Set();

for (const kw of flattenedKeywords) {
  const key = kw.keyword.toLowerCase().trim();
  if (!seen.has(key)) {
    seen.add(key);
    uniqueKeywords.push(kw);
  }
}

return {
  keywords: uniqueKeywords,
  count: uniqueKeywords.length
};

// Extract metrics from API response
const response = $input.first().json;
const keyword = $('Split Keywords Batch').item.json.keyword;

let metrics = {
  keyword: keyword,
  volume: 0,
  competition: 0,
  cpc: 0
};

if (response && response.data && response.data.length > 0) {
  const data = response.data[0];
  metrics = {
    keyword: keyword,
    volume: data.search_volume || 0,
    competition: data.competition || 0,
    cpc: data.cpc || 0
  };
}

return metrics;

// Analyze SERP competition
const serpData = $input.first().json;
const keywordData = $('Parse Metrics').item.json;

let brandPresence = 0;
let forumCount = 0;
let competitionScore = 0;

if (serpData && serpData.organic_results) {
  const results = serpData.organic_results.slice(0, 10);

  for (const result of results) {
    const domain = result.link ? new URL(result.link).hostname : '';

    // Check for big brands
    if (domain.includes('amazon.com') || domain.includes('etsy.com') || 
        domain.includes('target.com') || domain.includes('walmart.com')) {
      brandPresence++;
    }

    // Check for forums/low authority
    if (domain.includes('reddit.com') || domain.includes('quora.com') || 
        domain.includes('forum') || domain.includes('blogspot.com')) {
      forumCount++;
    }
  }

  // Calculate competition score
  competitionScore = brandPresence - forumCount;
}

return {
  ...keywordData,
  brandPresence,
  forumCount,
  competitionScore
};

// Calculate final scores for all keywords
const allKeywords = $input.all();
const scoredKeywords = [];

for (const item of allKeywords) {
  const kw = item.json;

  // Calculate intent weight (buyer intent = 2x, informational = 1x)
  const buyerIntentWords = ['buy', 'purchase', 'shop', 'gift', 'present', 'order'];
  const hasBuyerIntent = buyerIntentWords.some(word => 
    kw.keyword.toLowerCase().includes(word)
  );
  const intentWeight = hasBuyerIntent ? 2 : 1;

  // Calculate final score
  const score = (kw.volume * intentWeight) / (kw.competition + kw.brandPresence + 1);

  scoredKeywords.push({
    ...kw,
    intentWeight,
    score: Math.round(score * 100) / 100
  });
}

// Sort by score (highest first)
scoredKeywords.sort((a, b) => b.score - a.score);

return {
  keywords: scoredKeywords,
  topKeywords: scoredKeywords.slice(0, 10)
};

