  emailBody += `   ðŸ“ Suggested Title: ${kw.suggestedTitle || 'N/A'}\n\n`;
}

emailBody += 'Reply with the number (1-5) of the keyword you want to create content for.\n\n';
emailBody += 'Best regards,\nYour SEO Assistant';

return {
  selectedKeywords,
  emailBody,
  subject: 'ðŸŽ¯ SEO Keyword Opportunities - Action Required'
};

// Extract chosen keyword from email reply
const emails = $input.all();
const selectedKeywords = $('AI Select Best').item.json.selectedKeywords;

let chosenKeyword = null;

for (const email of emails) {
  const subject = email.json.subject || '';
  const body = email.json.text || '';

  // Look for reply to our notification
  if (subject.includes('Re:') && body.includes('SEO Keyword Opportunities')) {
    // Extract number from reply
    const numberMatch = body.match(/([1-5])/);
    if (numberMatch) {
      const index = parseInt(numberMatch[1]) - 1;
      if (selectedKeywords[index]) {
        chosenKeyword = selectedKeywords[index];
        break;
      }
    }
  }
}

return {
  chosenKeyword,
  hasReply: !!chosenKeyword
};
